	};

	componentDidMount() {
		console.log('[Persons.js] Inside componentDidMount()');
	};
	render() {
		return (
			<div className={ classes.Person }>
				<p onClick={ this.props.click }>I am { this.props.name } and I am { this.props.age } years old. I am { this.props.cool }.</p>
