import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
	state = {
		persons: [
			{ name: 'Ham', age: 27 },
			{ name: 'Karl', age: 22 },
			{ name: 'Barl', age: 31 }
		]
	}
	render() {
    return (
      <div className="App">
				<h1>This is a React App</h1>
				<p>It works</p>
				<button>Switch Name</button>
				<Person name={ this.state.persons[0].name } age= { this.state.persons[0].age } cool="definitely cool" />
				<Person name={ this.state.persons[1].name } age= { this.state.persons[1].age } cool="not cool" />
				<Person name={ this.state.persons[2].name } age= { this.state.persons[2].age } cool="sort of cool" />
      </div>
		);
  };
};

export default App;
